import { defineStore } from 'pinia';
import env from '@/env.js';
import { ref } from 'vue';

export const useMovieStore = defineStore('movieStore', {
    state: () => ({
        movies: [],
        popularShowsUrl: `${env.baseUrl}tv/popular?${env.apikey}`,
        favMovies: JSON.parse(localStorage.getItem('favorite-movies')) || [],
        isFav: false,
    }),
    actions: {
        async getMovies(){
            const res = await fetch(this.popularShowsUrl);
            const data = await res.json();

            this.movies = data.results;
            // console.log("this.movies  = ", this.movies)
        },

        //addToFavs(fav){
          // this.favMovies.push(fav);
          // console.log("favmovies  ", this.favMovies)
        //},
       isFavActive(movie){
        /*
        console.log(this.favMovies)

        console.log(movie.name)

        console.log(JSON.parse(JSON.stringify(this.favMovies)));

        console.log(typeof JSON.parse(JSON.stringify(this.favMovies)))
        */

        console.log('-----------');

        /*
        function json2array(json){
            var result = [];
            var keys = Object.keys(json);

            keys.forEach(function(key){
                //index === 1 && result.push(json[key]);
                result.push(json[key]);
            });

            return result;
        }
        */

        //const favMoviesObject = json2array(JSON.parse(JSON.stringify(this.favMovies)))

        const favMoviesObject = Object.entries(JSON.parse(JSON.stringify(this.favMovies)));

        console.log(Object.values(favMoviesObject));
        console.log(typeof Object.values(favMoviesObject));


        return Object.values(favMoviesObject).some((item) => {
            // item.id === movie.id && console.log('----------------matched');

            return item.id === movie.id;
        });
        },
        toggleFav(movie){
           const isFavorite=ref(Boolean);
           isFavorite.value= this.favMovies.includes(movie);
           //console.log(this.favMovies.includes(movie))

           if(isFavorite.value){

            //const newFavMovies= this.favMovies.filter(m=>m.id!==movie.id);
            this.favMovies= this.favMovies.filter(m=>m.id!==movie.id);

            //this.favMovies=newFavMovies;

            //console.log("newss",newFavMovies);
            isFavorite.value=false;
             //console.log("if içinde ",isFavorite.value);

           }
           else{
           this.favMovies.push(movie);
           isFavorite.value=true;
            // console.log("else içinde",isFavorite.value);
