import {defineStore} from 'pinia'
import env from '@/env.js';
import {ref} from 'vue';



export const useMovieStore=defineStore('movieStore',{
    state:()=>({
        movies:[],
        popularShowsUrl:`${env.baseUrl}tv/popular?${env.apikey}`,
        favMovies: localStorage.getItem('favorited-movies') || [],
        isFav:false,
        //iFav:false

    }),
    actions: {
//const popularShowsUrl=`${env.baseUrl}tv/popular?${env.apikey}`;

        async getMovies(){
            const res=await fetch(this.popularShowsUrl);
            const data=await res.json();
            //console.log("data from action = ",data.results);
            this.movies=data.results;
            //console.log("this.movies  = ",this.movies)
        },

        //addToFavs(fav){
          // this.favMovies.push(fav);
          // console.log("favmovies  ", this.favMovies)
        //},
       isFavActive(movie){
              return this.favMovies.includes(movie);
             //return iFav;
        },
        toggleFav(movie){
           const isFavo=ref(Boolean);
           isFavo.value= this.favMovies.includes(movie);
           //console.log(this.favMovies.includes(movie))

           if(isFavo.value){

            //const newFavMovies= this.favMovies.filter(m=>m.id!==movie.id);
            this.favMovies= this.favMovies.filter(m=>m.id!==movie.id);

            //this.favMovies=newFavMovies;

            //console.log("newss",newFavMovies);
            isFavo.value=false;
             //console.log("if içinde ",isFavo.value);

           }
           else{
           this.favMovies.push(movie);
           isFavo.value=true;
            // console.log("else içinde",isFavo.value);
           }
           this.isFav=isFavo.value;
           console.log("list2 =",this.favMovies)
           //return isFavo;
           //return this.isFav;

           localStorage.setItem('favorited-movies', this.favMovies);
        }
    }
})



